        public static IList<IList<int>> ThreeSum(int[] nums)
        {
            List<List<int>> result = new List<List<int>>();

            for (int i = 0; i <nums.Length; i++)
            {
                for (int j = i+2; j < nums.Length; j++)
                {
                    if (nums[i]+nums[i+1]+nums[j]==0)
                    {
                        List<int> s = new List<int>();
                        s.Add(nums[i]);
                        s.Add(nums[j]);
                        s.Add(nums[i+1]);
                        result.Add(s);
                    }
                }
            }

            return result.ToArray();
        }
        public static IList<IList<int>> ThreeSum1(int[] nums)
        {
            List<List<int>> result = new List<List<int>>();
            Array.Sort(nums);
            if (nums.Length == 3)
            {
                if (nums[0] + nums[1] + nums[2] == 0)
                {
                    result.Add(new List<int> { nums[0], nums[1], nums[2] });
                }
            }
            else
            {


                for (int i = 0; i < nums.Length - 3; i++)
                {
                    int a = nums[i];
                    int start = i + 1;
                    int end = nums.Length - 1;
                    while (start < end)
                    {
                        int b = nums[start];
                        int c = nums[end];
                        if ((a + b + c) == 0)
                        {

                            result.Add(new List<int> { a, b, c });
                            start = start + 1;
                            end=end-1;
                        }
                        else if (a + b + c > 0)
                        {
                            end = end - 1;
                        }
                        else
                        {
                            start = start + 1;
                        }
                    }
                }
            }
            return result.ToArray();
        }

        }
